import React, { useState } from 'react'

const PopupCard = ({ visible, setVisible }) => {
  return (
    <div className={`popup-card card ${visible ? '' : 'invisible'}`}>
      <button
        onClick={() => setVisible(false)}
        type="button"
        class="btn-close"
        aria-label="Close"
      ></button>
    </div>
  )
}
export default PopupCard
