import React, { useState } from 'react'
import Popup from 'react-animated-popup'

const PopupCard = ({ visible }) => {
  return (
    <Popup visible={visible} onClose={() => setVisible(false)}>
      <p>I am a popup!</p>
    </Popup>
  )
}
export default PopupCard
